import React, { useState } from 'react';
import { useChatStore } from '../store/useChatStore';
import { toast } from 'react-toastify';

const Dashboard = ({ onEnterChatroom }) => {
  const { chatrooms, addChatroom, deleteChatroom } = useChatStore();
  const [title, setTitle] = useState('');

  const handleCreate = () => {
    if (title.trim()) {
      addChatroom(title);
      toast.success('Chatroom created!');
      setTitle('');
    }
  };

  const handleDelete = (id) => {
    deleteChatroom(id);
    toast.success('Chatroom deleted!');
  };

  return (
    <div className="p-6 max-w-lg mx-auto">
      <h2 className="text-2xl font-bold mb-4">Dashboard</h2>

      <div className="flex gap-2 mb-4">
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Chatroom name"
          className="border p-2 flex-1 rounded"
        />
        <button onClick={handleCreate} className="bg-green-600 text-white p-2 rounded">
          Create
        </button>
      </div>

      <div>
        {chatrooms.map((room) => (
          <div key={room.id} className="flex justify-between items-center mb-2 p-2 border rounded">
            <span>{room.title}</span>
            <div className="flex gap-2">
              <button
                onClick={() => onEnterChatroom(room.id)}
                className="bg-blue-600 text-white px-2 rounded"
              >
                Open
              </button>
              <button
                onClick={() => handleDelete(room.id)}
                className="bg-red-600 text-white px-2 rounded"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
