useEffect(() => {
  fetchCountries()
    .then(res => {
      if (res.status === 200) {
        const sorted = res.data.sort((a, b) => a.name.common.localeCompare(b.name.common));
        setCountries(sorted);
      } else {
        toast.error('Failed to load country list.');
      }
    })
    .catch(err => {
      console.error('API error:', err);
      toast.error('Something went wrong fetching countries.');
    });
}, []);
