import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import { toast } from 'react-toastify';

const OtpForm = ({ onSuccess }) => {
  const initialValues = { otp: '' };
  const validationSchema = Yup.object({
    otp: Yup.string().required('OTP is required').length(4, 'OTP must be 4 digits'),
  });

  const handleSubmit = (values) => {
    toast.success('OTP Verified!');
    setTimeout(() => { onSuccess(); }, 1000);
  };

  return (
    <div className="max-w-sm mx-auto mt-10 p-6 border rounded-lg shadow-lg">
      <h2 className="text-2xl font-bold mb-6">Enter OTP</h2>
      <Formik initialValues={initialValues} validationSchema={validationSchema} onSubmit={handleSubmit}>
        <Form className="space-y-4">
          <div>
            <label className="block mb-1">OTP</label>
            <Field type="text" name="otp" className="w-full border p-2 rounded" />
            <ErrorMessage name="otp" component="div" className="text-red-600 text-sm" />
          </div>
          <button type="submit" className="w-full bg-green-600 text-white p-2 rounded hover:bg-green-700">Verify</button>
        </Form>
      </Formik>
    </div>
  );
};
export default OtpForm;
