import React, { useEffect, useState } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import { fetchCountries } from '../services/countriesService';
import { toast } from 'react-toastify';

const LoginForm = ({ onOtpSent }) => {
  const [countries, setCountries] = useState([]);

  useEffect(() => {
    fetchCountries()
      .then(res => {
        if (res.status === 200 && Array.isArray(res.data)) {
          // Filter countries with valid idd.root and suffixes
          const countriesWithDialCodes = res.data.filter(
            country =>
              country.idd &&
              country.idd.root &&
              country.idd.suffixes &&
              country.idd.suffixes.length > 0
          );
          // Sort alphabetically by country name
          const sorted = countriesWithDialCodes.sort((a, b) =>
            a.name.common.localeCompare(b.name.common)
          );
          setCountries(sorted);
          console.log('Fetched countries:', sorted); // For debugging
        } else {
          toast.error('Failed to load country list.');
        }
      })
      .catch(err => {
        console.error('API error:', err);
        toast.error('Something went wrong fetching countries.');
      });
  }, []);

  const initialValues = { countryCode: '', phoneNumber: '' };

  const validationSchema = Yup.object({
    countryCode: Yup.string().required('Country code is required'),
    phoneNumber: Yup.string()
      .required('Phone number is required')
      .matches(/^\d{10}$/, 'Enter valid 10-digit number'),
  });

  const handleSubmit = (values) => {
    toast.success('OTP sent!');
    setTimeout(() => {
      onOtpSent(values.phoneNumber);
    }, 1500);
  };

  return (
    <div className="max-w-sm mx-auto mt-10 p-6 border rounded-lg shadow-lg">
      <h2 className="text-2xl font-bold mb-6">Login with Phone</h2>
      <Formik initialValues={initialValues} validationSchema={validationSchema} onSubmit={handleSubmit}>
        <Form className="space-y-4">
          <div>
            <label className="block mb-1">Country Code</label>
            <Field as="select" name="countryCode" className="w-full border p-2 rounded">
              <option value="">Select Country</option>
              {countries.length > 0 ? (
                countries.map((country) => (
                  <option
                    key={country.cca2}
                    value={country.idd.root + country.idd.suffixes[0]}
                  >
                    {country.flag} {country.name.common} ({country.idd.root}{country.idd.suffixes[0]})
                  </option>
                ))
              ) : (
                <option value="" disabled>
                  Loading countries...
                </option>
              )}
            </Field>
            <ErrorMessage name="countryCode" component="div" className="text-red-600 text-sm" />
          </div>

          <div>
            <label className="block mb-1">Phone Number</label>
            <Field type="text" name="phoneNumber" className="w-full border p-2 rounded" />
            <ErrorMessage name="phoneNumber" component="div" className="text-red-600 text-sm" />
          </div>

          <button type="submit" className="w-full bg-blue-600 text-white p-2 rounded hover:bg-blue-700">
            Send OTP
          </button>
        </Form>
      </Formik>
    </div>
  );
};

export default LoginForm;
